import axios from "axios";
import React from "react";
import { useState } from "react";

function Signup() {
 const [form,setForm]=useState({
  name:"",
  email:"",
  password:""

 })

 const onchange=(e)=>{
  setForm({
    ...form,id:new Date().getTime().toString(),[e.target.name]:e.target.value
  })
 }
 const submit =async(e)=>{
const res = await axios.post('http://localhost:3000/user')
.then(()=>{
  setForm({
    name:"",
  email:"",
  password:""
  })
})
 }
  return (
    <div>
      <form>
      <div className="mb-3">
          <label htmlFor="exampleInputEmail1" className="form-label">
            Name 
          </label>
          <input
            type="name"
            className="form-control"
            id="exampleInputEmail1"
            aria-describedby="emailHelp"
            name="name"
            value={form.name}
            onChange={onchange}
          />
         
        </div>

        <div className="mb-3">
          <label htmlFor="exampleInputEmail1" className="form-label">
            Email address
          </label>
          <input
            type="email"
            className="form-control"
            id="exampleInputEmail1"
            aria-describedby="emailHelp"
            name="email"
            value={form.email}
            onChange={onchange}
          />
         
        </div>
        <div className="mb-3">
          <label htmlFor="exampleInputPassword1" className="form-label">
            Password
          </label>
          <input
            type="password"
            className="form-control"
            id="exampleInputPassword1"
            name="password"
            value={form.password}
            onChange={onchange}

          />
        </div>
       
        <button type="submit" className="btn btn-primary" onClick={submit}>
          Submit
        </button>
      </form>
    </div>
  );
}

export default Signup;
